import { useState, useEffect } from "react";
import logo from "../assets/Logo.png";

const Navigation = ({ account, setAccount }) => {
  const [theme, setTheme] = useState("light"); // ThemeState

  useEffect(() => {
    document.body.className = theme; // Cambia la clase del body cuando el tema cambia
  }, [theme]);

  const connectHandler = async () => {
    const accounts = await window.ethereum.request({
      method: "eth_requestAccounts",
    });
    setAccount(accounts[0]);
  };

  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light"); // toggle Theme
  };

  return (
    <nav>
      <ul className="nav__links">
        <li>
          <a href="#">Buy</a>
        </li>
        <li>
          <a href="#">Sell</a>
        </li>
      </ul>

      <div className="nav__brand">
        <img src={logo} alt="Logo" />
        <h1>George Brown Huskies DEMO</h1>
      </div>

      <button onClick={toggleTheme} className="nav__theme-toggle">
        {theme === "light" ? "Dark Mode" : "Light Mode"}
      </button>

      {account ? (
        <button type="button" className="nav__connect">
          {account.slice(0, 6) + "..." + account.slice(38, 42)}
        </button>
      ) : (
        <button type="button" className="nav__connect" onClick={connectHandler}>
          Connect
        </button>
      )}
    </nav>
  );
};

export default Navigation;
